#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
  clock_t begin = clock();
  /*vector<string> input = {{"ULL"},
                          {"RRDDD"},
                          {"LURDL"},
                          {"UUUUD"}};*/
  vector<string> input = {{""},
                          {""},
                          {""},
                          {""},
                          {""}};;
  const char field[3][3] = {{'1','2','3'},
                            {'4','5','6'},
                            {'7','8','9'}};
  short x = 1, y = 1, len = input.size();
  string answer = "";
  
  for(int i = 0; i < len; i++) {
    for(int j = 0; j < input[i].size(); j++) {
      switch(input[i][j]) {
        case 'U':
          y = (y - 1 >= 0 ? y - 1 : 0);
        break;
        case 'R':
          x = (x + 1 <= 2 ? x + 1 : 2);
        break;
        case 'D':
          y = (y + 1 <= 2 ? y + 1 : 2);
        break;
        case 'L':
          x = (x - 1 >= 0 ? x - 1 : 0);
        break;
      }
    }
    answer+=field[y][x];
  }

  clock_t end = clock();
  printf("RÃ©sultat %s\n",answer.data()); // 65556
  printf("Duration = %f s\n",(double)(end - begin) / CLOCKS_PER_SEC);
}
